select timestamp, events as errorMessage, serviceName, name, kind, durationNano, dbSystem, dbName, dbOperation, httpMethod, httpCode, httpRoute, hasError , stringTagMap['os.type'] as os, stringTagMap['host.name'] as host, stringTagMap['db.system'] as dbtype, stringTagMap['db.dboperation'] as dbop, stringTagMap['telemetry.sdk.language'] as telemetryLanugage, stringTagMap['net.sock.peer.name'] as peerDomain, stringTagMap['net.sock.host.port'] as peerPort, stringTagMap['db.statement'] as dbStatements, stringTagMap['telemetry.sdk.name'] as otlSdk, stringTagMap['db.connection_string'] as connectionString, stringTagMap['http.client_ip'] as clientIp, stringTagMap['db.sql.table'] as dbTable
from signoz_traces.distributed_signoz_index_v2
1=1
and dbSystem =#{params.dbtype}
and hasError =#{params.hasError}
and serviceName like concat( #{params.serviceName}, '%')
and ((stringTagMap['name'] = #{params.name}) or (name=#{params.name}))
and timestamp >= #{params.timeStart}
and #{params.timeEnd} >= timestamp
order by timestamp desc
limit #{params.offSet}, #{params.perPage};

