mapper.select:
select id,dict_type_name,tenant_id,comment,create_time,update_time,is_active,created_by,updated_by,dict_type_key from common_dict_type
where 1=1
and  delete_time=0 and is_active=1 and dict_type_key!='DATA_ACCESS_PERMISSION'

    and  tenant_id= #{params.tenantId}


    and dict_type_name like '%${params.dict_type_name}%'


    and dict_type_key  like '%${params.dict_type_key}%'


order by  #{params.orderField}


 #{params.order}

select count(1) from common_dict_type
where 1=1
and  delete_time=0

    and  tenant_id= #{params.tenantId}


    and dict_name like '%${params.dict_name}%'


    and dict_key  like '%${params.dict_key}%'


order by  #{params.orderField}


 #{params.order}

mapper.update:
update common_dict_type

    update_time = now()
     , dict_type_name = #{params.dictTypeName}
    , comment = #{params.comment}
    , is_active = #{params.isActive}
    , dict_type_key = #{params.dictTypeKey}

where id = #{params.id}

mapper.select:
SELECT t2.id, t2.dict_name, t2.dict_type_id, t2.tenant_id, t2.dict_value, t2.COMMENT, t2.create_time, t2.update_time, t2.is_active, t2.created_by, t2.updated_by, t2.dict_key, t2.delete_time FROM common_dict AS t2, common_dict_type AS t1
AND t2.is_active = #{isActive}
AND t1.id = t2.dict_type_id AND t2.delete_time = 0 AND t2.tenant_id = t1.tenant_id AND t1.dict_type_key = #{dictTypeKey,jdbcType=VARCHAR} AND t1.tenant_id = #{tenantId,jdbcType=VARCHAR}
